    public static void play() throws InterruptedException {
        int delay = 800;
        for (String frame : FRAMES) {
            clearConsole();
            System.out.println(frame);
            Thread.sleep(delay);
        }
    }

    public static void clearConsole() {
        System.out.print("\033c");
        System.out.flush();
    }
